@preprocessor   stylus
@var select theme "Theme" ["latte:Latte", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accent "Accent" {"Rosewater": "'rosewater'", "Flamingo": "'flamingo'", "Pink": "'pink'", "Mauve": "'mauve'", "Red": "'red'", "Maroon": "'maroon'", "Peach": "'peach'", "Yellow": "'yellow'", "Green": "'green'", "Teal": "'teal'", "Blue*": "'blue'", "Sapphire": "'sapphire'", "Sky": "'sky'", "Lavender": "'lavender'"}
==/UserStyle== */

@-moz-document regexp("https?://(canary\\.|ptb\\.|)discord.com/.*") {
    @import url("https://catppuccin.github.io/discord/dist/catppuccin-" + theme + "-" + accent + ".theme.css")
}

/* ==UserStyle==
@name         YT Full Header + Hover 2023
@version      1.0
@namespace    userstyles.world/user/hkc
@description  Entire FULLSCREEN Windowed Youtube + Search hover bar


@author       Runy
@license      No License
==/UserStyle== */
@-moz-document url-prefix("https://www.youtube.com/watch?v") {
    ytd-watch-flexy[theater]:not([fullscreen]) #player-full-bleed-container.ytd-watch-flexy {
        height: calc(100.5vh);
        max-height: calc(100.5vh);
    }

    ytd-watch-flexy[full-bleed-player] #columns.ytd-watch-flexy {
        margin-top: 175px;
    }
    ytd-player#ytd-player,
    #movie_player.html5-video-player.ytp-autonav-endscreen-cancelled-state.ended-mode,
    #movie_player.html5-video-player.ytp-fullscreen {

        background-color: #000;
    }

    #masthead-container.ytd-app {
        margin-top: 1px;
        padding-bottom: 5px;
        opacity: 0;
        transition: margin 250ms, padding 250ms, opacity 500ms;
    }
    #masthead-container.ytd-app:hover {
        margin-top: 0;
        padding-bottom: 0;
        opacity: 1;
        background: #0f0f0f
    }
    #page-manager.ytd-app {
        margin-top: -6px;
    }
}
