    // TWITTER MEDIA DOWNLOADER

    body[data-nightmode="true"] .twMediaDownloader_media_button button.btn,
    .twMediaDownloader_media_button button.btn {
        background-color: $surface0;
        border-color: $surface2;
        color: $overlay2;

        &:hover {
            color: $text;
            background-color: $surface1;
            border-color: $overlay1
        }
    }

    .twMediaDownloader_download_button_container {
        bottom: 0 !important;
    }

    .twMediaDownloader_download_button {
        color: $accent;
        text-decoration: none;
        font-family: "TwitterChirp", "Meiryo", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
        font-weight: 400;
        font-size: 12px;
    }

    #twMediaDownloader_container,
    #twMediaDownloader_container.night_mode {
        .btn {
            background-color: $surface0;
            color: $text;
            border-color: $surface1;

            &[disabled] {
                color: $text;
                background-color: $surface0;
                background-image: linear-gradient($overlay1, $surface0);
                border-color: $surface1;
            }
        }

        .twMediaDownloader_dialog {
            background: $crust;

            .twMediaDownloader_toolbox {
                background: $base;

                .range_container {
                    h3 {
                        color: $subtext0;
                    }

                    table {
                        color: $text;
                    }

                    input {
                        padding: 4px;
                        color: $text;

                        &.tweet_id,
                        &.tweet_number {
                            color: $overlay1;
                            background: $mantle;
                            border: solid 1px $surface0;
                        }
                    }
                }

                .twMediaDownloader_button_container {
                    label {
                        color: $text;
                    }
                }
            }

            .twMediaDownloader_status {
                background: $mantle;

                .twMediaDownloader_log {
                    color: $text;
                    background: $base;
                    border: inset $crust 1px;

                    .log-item {
                        text-shadow: unset;

                        a.tweet-link {
                            color: $red;
                        }

                        a.media-link {
                            color: $blue;
                        }
                    }
                }
            }
        }
    }

}

/* ==UserStyle==
@name           Reddit Catppuccin
@namespace      github.com/catppuccin/reddit
@version        1.0.0
@description    Soothing pastel theme for Reddit.
@author         jayylmao
@updateURL      https://github.com/catppuccin/reddit/raw/main/reddit-catppuccin.user.css

@preprocessor stylus

@var select stylusAccent "Accent color" [
	"Rosewater",
	"Flamingo",
	"Pink",
	"Mauve",
	"Red",
    "Maroon",
	"Peach",
	"Yellow",
    "Green",
	"Teal",
	"Sky",
    "Sapphire",
	"Blue",
	"Lavender"
]

@var select stylusFlavor "Catppuccin Flavor" [
    "Latte",
    "Frappe",
    "Macchiato",
    "Mocha"
]
==/UserStyle== */

@-moz-document domain("reddit.com") {
    :root {
        if stylusFlavor=="Latte" {
            --rosewater: #dc8a78;
            --flamingo : #dd7878;
            --pink     : #ea76cb;
            --mauve    : #8839ef;
            --red      : #d20f39;
            --maroon   : #e64553;
            --peach    : #fe640b;
            --yellow   : #df8e1d;
            --green    : #40a02b;
            --teal     : #179299;
            --sky      : #04a5e5;
            --sapphire : #209fb5;
            --blue     : #1e66f5;
            --lavender : #7287fd;
            --text     : #4c4f69;
            --subtext1 : #5c5f77;
            --subtext0 : #6c6f85;
            --overlay2 : #7c7f93;
            --overlay1 : #8c8fa1;
            --overlay0 : #9ca0b0;
            --surface2 : #acb0be;
            --surface1 : #bcc0cc;
            --surface0 : #ccd0da;
            --base     : #eff1f5;
            --mantle   : #e6e9ef;
            --crust    : #dce0e8;

            /* non-standard colors */
            --surface1Transparent: #bcc0ccbd;
            --mist: #dce0e8bd; 
        }
        else if stylusFlavor=="Frappe" {
            --rosewater: #f2d5cf;
            --flamingo : #eebebe;
            --pink     : #f4b8e4;
