            /* non-standard colors */
            --surface1Transparent: #494d64bd;
            --mist: #181926bd;
        }
        else if stylusFlavor=="Mocha" {
            --rosewater: #f5e0dc;
            --flamingo : #f2cdcd;
            --pink     : #f5c2e7;
            --mauve    : #cba6f7;
            --red      : #f38ba8;
            --maroon   : #eba0ac;
            --peach    : #fab387;
            --yellow   : #f9e2af;
            --green    : #a6e3a1;
            --teal     : #94e2d5;
            --sky      : #89dceb;
            --sapphire : #74c7ec;
            --blue     : #89b4fa;
            --lavender : #b4befe;
            --text     : #cdd6f4;
            --subtext1 : #bac2de;
            --subtext0 : #a6adc8;
            --overlay2 : #9399b2;
            --overlay1 : #7f849c;
