		@mantle: @catppuccin[@@lookup][@mantle];
		@crust: @catppuccin[@@lookup][@crust];
		@accent-colour: @catppuccin[@@lookup][@@accent];

		@bb: @catppuccin[@@lookup][@bb];
		@bk: @catppuccin[@@lookup][@bk];
		@bn: @catppuccin[@@lookup][@bn];
		@bp: @catppuccin[@@lookup][@bp];
		@bq: @catppuccin[@@lookup][@bq];
		@br: @catppuccin[@@lookup][@br];
		@wb: @catppuccin[@@lookup][@wb];
		@wk: @catppuccin[@@lookup][@wk];
		@wn: @catppuccin[@@lookup][@wn];
		@wp: @catppuccin[@@lookup][@wp];
		@wq: @catppuccin[@@lookup][@wq];
