      @overlay1: #7f849c;
      @overlay0: #6c7086;
      @surface2: #585b70;
      @surface1: #45475a;
      @surface0: #313244;
      @base: #1e1e2e;
      @mantle: #181825;
      @crust: #11111b;
    }
  }

  #catppuccin(@theme);

  #catppuccin(@lookup) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    body {
      background: @base;
      color: @text;

      header h1 a,
      header h1 span {
        color: @green;
        text-shadow: 0 0 2px @green, 0 0 5px @green;
      }

      a {
        color: @green;
      }

      .star-count {
        color: @yellow;
      }

      .quiet {
        color: @subtext0;
        opacity: 1;
      }

      .share,
      .share:hover,
      input[type="submit"] {
        color: @green;
      }

      main {
        article {
          em {
            color: @text;
            text-shadow: 0 0 5px @text;
          }

          h2 {
            color: @text;
          }


          input[type="radio"]:checked ~ span,
          input[type="checkbox"]:checked ~ span {
            color: @text;
          }

          input[type="radio"] ~ span:hover,
          input[type="radio"] ~ span:focus,
          input[type="checkbox"]:hover ~ span,
          input[type="checkbox"]:focus ~ span {
            background-color: @crust;
          }
        }
        .supporter-badge {
          color: @yellow;
        }

        .supporter-badge:hover {
          color: @yellow;
          text-shadow: 0 0 5px @yellow;
        }

        .sponsor-badge {
          color: @blue;
        }

        .sponsor-badge:hover,
        .sponsor-badge:focus {
          color: @blue;
          text-shadow: 0 0 5px @blue;
        }

        .leaderboard-entry {
          .leaderboard-position {
            color: @subtext1;
          }
          .leaderboard-totalscore {
            color: @text;
          }
        }

        .leaderboard-daylinks-selected {
          color: @text;
          text-shadow: 0 0 5px @text;
        }

        .leaderboard-daylinks-selected:hover {
          color: @green;
        }

        .leaderboard-anon {
          opacity: 1;
          color: @subtext0;
        }
        .calendar {
          .calendar-color-w {
            color: @text;
          }
          .calendar-color-s {
            color: @yellow;
          }
          .calendar-color-c {
            color: @subtext1;
          }
          .calendar-color-g3 {
            color: darken(@green, 3);
          }
          .calendar-color-g2 {
            color: @green;
          }
          .calendar-color-g4 {
            color: darken(@green, 3.5);
          }
          .calendar-color-u {
            color: @sky;
          }
          .calendar-color-a {
            color: @subtext0;
          }
          .calendar-color-g1 {
            color: darken(@green, 2.5);
          }
          .calendar-color-g0 {
            color: darken(@green, 2);
          }
          .calendar-color-l {
            color: @red;
          }
          .calendar-mark-complete {
            color: @yellow;
          }
          .calendar-mark-verycomplete {
            color: @yellow;
          }
          .calendar-day {
            color: @subtext1;
          }
          #calendar-countdown {
            color: @subtext1;
          }

          span {
            color: @surface0;
          }

          a {
            color: @surface2;
          }
        }

        .day-success {
          color: @yellow;
          text-shadow: 0 0 5x @yellow;
        }

        input[type="text"],
        textarea,
        code::before {
          border-color: @surface0;
          background: @crust;
        }
      }
    }
  }
}

/* ==UserStyle==
@name YouTube Catppuccin
@namespace github.com/catppuccin/userstyles/styles/youtube
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/youtube
@version 3.0.9
@description Soothing pastel theme for YouTube
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve", "red:Red*", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
@var checkbox logo "Logo" 0
@var checkbox oledOn "Enable black bars" 0
==/UserStyle== */
@-moz-document regexp('https?:\\/\\/(?!gaming\\.)(?!consent\\.)(?!vr\\.)(?!music\\.)(?!tv\\.)(?!artists\\.)(?!studio\\.).*youtube(?!kids\\.).*\\.com\\/(?!premium|howyoutubeworks).*$') {
  /* youtube.com */
  :root[dark] {
    #catppuccin(@darkFlavor, @accentColor);
    #oled(@oledOn);
    #logo(@logo);
  }
  :root {
    #catppuccin(@lightFlavor, @accentColor);
    #oled(@oledOn);
    #logo(@logo);
  }

  /* for oled monitors */
  #oled(@oled) when (@oled =0) {
    #ytd-player #container {
      background: @base !important;
    }
  }

  #logo(@logo) {
    ytd-topbar-logo-renderer.style-scope a svg > g,
    svg.ytd-consent-bump-v2-lightbox > g:nth-child(1) {
      path[fill="#FF0000"] {
        fill: @accent-Color !important;
      }
      path[fill="white"] {
        fill: @base !important;
      }
    }
    & when (@logo =1) {
      ytd-topbar-logo-renderer,
      svg.ytd-consent-bump-v2-lightbox {
        display: none;
      }
    }
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
